const fetch = require(`node-fetch`)

async function generateQuote(options = {}) {
   const url = await fetch('https://api.quotable.io/random').then(response => response.json())

   const {
    author,
    content
} = url

       const Data = {
         embed: {
             color: options.Color || "RANDOM",
             title: "here's a random quote",
             description: `\`${content}\` - ${author}`,
             timestamp: new Date()
         }
     };
 

     return Data;

} 
module.exports = generateQuote