async function getfoxgirl(options = {}) {
    const Fetch = require(`node-fetch`)
    const res = await Fetch("https://nekos.life/api/v2/img/fox_girl"),
        json = await res.json();

    if (!json.url) throw new Error(`Something Went Wrong, Try Again Later!`);

    const Data = {
        embed: {
            color: options.Color || "RANDOM",
            description: "Random Foxgirl img",
            image: {
                url: json.url
            },
            timestamp: new Date()
        }
    };

    if (options.ResultOnly && options.ResultOnly === true) return {
        Result: json.url
    };

    return Data;
};
module.exports = getfoxgirl